export default {
  data() {
    return {
      longitudeInPixelsToTranslateX: 21,
      numberOfCards: 8,
      isClosed: true,
    }
  },
  methods: {
    shuffleTheCards(index) {
      // Open the cards
      if (this.isClosed)
        this.isClosed = false
      // Close the cards
      if (!this.isClosed && index === 0)
        this.isClosed = true
    },
  },
}
</script>

<template>
  <button @click="isClosed = !isClosed">
    open
  </button>
  <section class="card-list">
    <article v-for="(card, index) in numberOfCards" :key="index" class="card" :class="isClosed ? 'closed' : 'opened'" @click="shuffleTheCards(index)">
      <div class="card-author" />
    </article>
  </section>
</template>

